import React, {useState, useEffect} from 'react'
import config from '../../config.json'
import EventsTable from './EventsTable'
import CsvDownload from 'react-json-to-csv'

function Events(user) {
        // console.log(user)
        let BaseUrl = config.baseUrl + "/api/events"
        let EventUrl = config.baseUrl + "/api/events/all"

        const [events, setEvents] = useState([])

        let key = JSON.parse(localStorage.getItem("apiKey"))

        let fetchData = async(url) =>{
            let response = await fetch(url, {
                method: 'GET', // *GET, POST, PUT, DELETE, etc.
                mode: 'cors', // no-cors, *cors, same-origin
                cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
                // credentials: 'include', // include, *same-origin, omit
                headers: {
                  'Content-Type': 'application/json',
                  "Authorization": key
                //   'Content-Type': 'application/x-www-form-urlencoded',
                },
              })
            let resp = await response.json()
            return resp
        }

        let deleteItem = async (url) => {
            let response = await fetch(url, {
                method: 'DELETE', // *GET, POST, PUT, DELETE, etc.
                mode: 'cors', // no-cors, *cors, same-origin
                cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
                // credentials: 'include', // include, *same-origin, omit
                headers: {
                  'Content-Type': 'application/json',
                  "Authorization": key
                //   'Content-Type': 'application/x-www-form-urlencoded',
                },
              })
            let resp = await response.json()
            return resp
        }

        const [csvData, setCsvData] = useState([]);

        async function makeCSV(response) {
            let arr = [];
            response.map((item)=> {
                let obj = {};
                obj.Name = item.first_name;
                obj.Phone = item.phone_number;
                arr.push(obj);
            })
            setCsvData(arr);
        }

        useEffect(()=>{
            fetchData(EventUrl).then((response)=>{
                console.log(response);
                if (response.success) {

                    let resp = response.response;

                    makeCSV(resp);

                    console.log("success",response)
                    setLeads(response.response)    
                    let currentPage = Page.current
                    let totalPage = parseInt(response.count/50)
                    if (response.count%50) {
                        totalPage += 1
                    }

            } else {
                console.log(response)
            }    
        })},[])

        useEffect(()=>{
            fetchData(LeadUrl+'?page='+Page.current).then((response)=>{

                if (response.success) {
                console.log("success",response)
                makeCSV(response.response);
                setLeads(response.response)    
            } else {
                console.log(response)
            }    
        })},[Page])


        async function removeItem(item){
            let response = await deleteItem(BaseUrl+"/"+item._id)
            if(response.success){
                setLeads(leads.filter((lead)=>lead._id !== item._id))
                console.log("remove")
                let currentPage = Page.current
                //checking pagination of the last item deletion in the last page
                if ((Page.count-1) % 50 === 0 && Page.current ===  parseInt((Page.count-1)/50)) {
                    currentPage -= 1
                }

                if (Page.count % 50 && Page.totalPage != parseInt((Page.count-1)/50)){
                    Page.totalPage -= 1
                }


                setPage({
                    count: Page.count - 1 ,
                    current: currentPage
                })
            } else {
                console.log("Cannot delete")
            }

        }






    return (
        <div className="container-fluid">

                    <h1 className="h3 mb-2 text-gray-800 heading">Leads Table</h1> <CsvDownload data={csvData} />

                    <div className="card shadow mb-4">
                        <div className="card-header py-3">
                            <h6 className="m-0 font-weight-bold text-primary">Leads</h6>
                        </div>
                                <div className="container" width="100%">

                                    {leads.map((item)=>(
                                        <EventsTable key={item._id} item={item} remove={removeItem}></EventsTable>
                                    ))}
                                </div>
                    </div>

    </div>


    )
}

export default Leads
