import React, {useState, useEffect} from 'react'
import config from '../../config.json'
import EventsTableItem from './EventsTableItem'
import Create from './Create'


function Events() {

    const [events, setEvents] = useState([])
    const [options, setOptions] = useState(0)
    const [details, setDetails] = useState({})
    const [types, setTypes] = useState([])

    const [members, setMembers] = useState([])

    let key = JSON.parse(localStorage.getItem("apiKey"))

    let url = config.baseUrl+"/api/events"
    let typesUrl = config.baseUrl+"/api/events"

    // let membersUrl = config.baseUrl+"/api/members"

    let fetchData = async(url) =>{
        let response = await fetch(url, {
            method: 'GET', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            // credentials: 'include', // include, *same-origin, omit
            headers: {
              'Content-Type': 'application/json',
              "Authorization": key
            //   'Content-Type': 'application/x-www-form-urlencoded',
            },
          })
        let resp = await response.json()
        return resp
    }

    let postData = async(url) =>{
        let response = await fetch(url, {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            // credentials: 'include', // include, *same-origin, omit
            headers: {
              'Content-Type': 'application/json',
              "Authorization": key
            //   'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify(details)
          })
        let resp = await response.json()
        return resp
    }

    let deleteData = async(url) => {
        let response = await fetch(url, {
            method: 'DELETE',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json',
                "Authorization": key
            }
        })
    }

    useEffect(()=>{
        fetchData(url+"/all").then(async(response)=>{
            console.log("response",response)
            if (response.success){
                console.log(response.response)
                setEvents(response.response)
            } else {
                console.log("unable to fetch data!")
            }

        })
    })

    useEffect(()=>{
        fetchData(url+"/all").then(async(response)=>{
            console.log("response",response)
            if (response.success){
                console.log(response.response)
                setEvents(response.response)
            } else {
                console.log("unable to fetch data!")
            }

        })
    })

    let submitHandler = (e) => {
        e.preventDefault();
        console.log("This is here",events)
        postData(url+"/create").then(async(response)=> {
            if(response.success) {
                setCategories([...events,events])
                e.target.name.value = null
                e.target.type.value = ""
                e.target.price.value = 0
                e.target.description.value = null
                setDetails({
                    name: null,
                    type: "",
                    price: 0,
                    description: null
                })
            } else {
                console.log(response)
                console.log("Cannot create")
            }

        })
        console.log("I am here")
        console.log(details)
    }

    return (
        <div className="container-fluid">
                <div className="row">
                    <button className="col-3 btn btn-link" onClick={()=>setOptions(0)}>All Events</button>
                    <button className="col-3 btn btn-link" onClick={()=>setOptions(1)}>Create Event</button>
                    <button className="col-3 btn btn-link" onClick={()=>setOptions(2)}>Delete Events</button>
                </div>
                {options==0? <>{events.forEach(event => {
                    <EventsTableItem item={event} remove={deleteData} deleteUrl={url}></EventsTableItem>
                })}</>:''}
                {options==1? <Create submitHandler={submitHandler} setDetails={setDetails} details={details}></Create>:''}



        </div>)
}

export default Events
