import React, {useState, useEffect} from 'react'
import config from '../../config.json'
import EventOptions from './EventOptions'
import EditEvent from './EditEvent'

function EventPage({event, deleteEvent, setPageOptions, types, details, setDetails}) {
    const [options, setOptions] = useState(0)

    let editEvent = async () => {

    }

    const dateArr = Date(event.date).split(" ")
    let dateString = ""
    dateArr.forEach((item, i)=> i<4? dateString+=item+" ": dateString+="")


    return <div className="card">
    <h5 className="card-header">{event.name}</h5>
    <div className="card-body">
        <img className="card-image" src={event.images.length>0? config.baseUrl+"/"+event.images[0].url:''}></img>
      <h5 className="card-title">{dateString}</h5>
      <h5 className="card-title">{event.type.name}</h5>
      <h5 className="card-title">Total participants: {" "+event.registrations.length}</h5>
      <p className="card-text">{event.details}</p>
    </div>
    {options == 0? <EventOptions event={event} setOptions={setOptions} setPageOptions={setPageOptions} deleteEvent={deleteEvent}></EventOptions>:''}
    {options == 3? <EditEvent submitHandler={editEvent} event={event} types={types} details={details}></EditEvent>:''}
  </div>

}

export default EventPage
